            this.details_ = d;
            var dt = $(hid + '#detailstop', sdoc);
            if (dt) dt.style.display = d ? (BROWSER.IE && BROWSER.VERSION < 8 ? 'block' : 'table') : 'none';
            var s = hdl.GetSkin('start_stop_page');
